diff --git a/Modules/_ctypes/callproc.c b/Modules/_ctypes/callproc.c
index 352487ed964..0e3238e9f09 100644
--- a/Modules/_ctypes/callproc.c
+++ b/Modules/_ctypes/callproc.c
@@ -1025,8 +1025,11 @@ static PyObject *GetResult(ctypes_state *st,
            O_get), we have to call Py_DECREF because O_get has already
            called Py_INCREF.
         */
-        if (info->getfunc == _ctypes_get_fielddesc("O")->getfunc) {
-            Py_DECREF(retval);
+// FIXME: Crash CWE-825: Expired Pointer Dereference
+         if (info->getfunc == _ctypes_get_fielddesc("O")->getfunc) {
+            if (retval != NULL) {
+                Py_DECREF(retval);
+            }
         }
     }
     else {
@@ -1042,6 +1045,7 @@ static PyObject *GetResult(ctypes_state *st,
     return v;
 }

