patch:
diff --git a/Modules/_ctypes/stgdict.c b/Modules/_ctypes/stgdict.c
index ad82e4891c5..16960783ce6 100644
--- a/Modules/_ctypes/stgdict.c
+++ b/Modules/_ctypes/stgdict.c
@@ -568,8 +568,12 @@ PyCStructUnionType_update_stginfo(PyObject *type, PyObject *fields, int isStruct
     if (!isStruct) {
         size = union_size;
     }
-
+// FIXME: Crash CWE-369: Divide By Zero
+ 
     /* Adjust the size according to the alignment requirements */
+    if (total_align == 0) {
+        total_align = 1;  /* Set default alignment to prevent division by zero */
+    }
     aligned_size = ((size + total_align - 1) / total_align) * total_align;

     if (isStruct) {
@@ -580,8 +584,7 @@ PyCStructUnionType_update_stginfo(PyObject *type, PyObject *fields, int isStruct
         padding = aligned_size - size;
         if (padding > 0) {
             ptr = stginfo->format;
-            stginfo->format = _ctypes_alloc_format_padding(ptr, padding);
-            PyMem_Free(ptr);
+            stginfo->format = _ctypes_alloc_format_padding(ptr, padding);            PyMem_Free(ptr);
             if (stginfo->format == NULL) {
                 return -1;
             }
