diff --git a/Objects/descrobject.c b/Objects/descrobject.c
index 268af0b217c..09af36ee6d5 100644
--- a/Objects/descrobject.c
+++ b/Objects/descrobject.c
@@ -144,7 +144,12 @@ method_get(PyObject *self, PyObject *obj, PyObject *type)
     if (descr_check((PyDescrObject *)descr, obj) < 0) {
         return NULL;
     }
-    if (descr->d_method->ml_flags & METH_METHOD) {
+// FIXME: Crash CWE-825: Expired Pointer Dereference
+     if (descr->d_method->ml_flags & METH_METHOD) {
+        if (type == NULL) {
+            PyErr_SetString(PyExc_TypeError, "descriptor 'method' needs a non-NULL type as arg 2");
+            return NULL;
+        }
         if (PyType_Check(type)) {
             return PyCMethod_New(descr->d_method, obj, NULL, descr->d_common.d_type);
         } else {
@@ -159,6 +164,7 @@ method_get(PyObject *self, PyObject *obj, PyObject *type)
     }
 }

