patch:
diff --git a/Python/import.c b/Python/import.c
index 9dec0f488a3..3f76bcf03c5 100644
--- a/Python/import.c
+++ b/Python/import.c
@@ -3845,7 +3845,8 @@ PyImport_ImportModuleLevelObject(PyObject *name, PyObject *globals,
                 Py_DECREF(front);
             }
             else {
-                Py_ssize_t cut_off = len - dot;
+// FIXME: Crash CWE-416: Use After Free
+                 Py_ssize_t cut_off = len - dot;
                 Py_ssize_t abs_name_len = PyUnicode_GET_LENGTH(abs_name);
                 PyObject *to_return = PyUnicode_Substring(abs_name, 0,
                                                         abs_name_len - cut_off);
@@ -3854,15 +3855,16 @@ PyImport_ImportModuleLevelObject(PyObject *name, PyObject *globals,
                 }

                 final_mod = import_get_module(tstate, to_return);
-                Py_DECREF(to_return);
                 if (final_mod == NULL) {
                     if (!_PyErr_Occurred(tstate)) {
                         _PyErr_Format(tstate, PyExc_KeyError,
                                       "%R not in sys.modules as expected",
                                       to_return);
                     }
+                    Py_DECREF(to_return);
                     goto error;
                 }
+                Py_DECREF(to_return);
             }
         }
         else {
