diff --git a/quickjs.c b/quickjs.c
index 46e124a..d09fd78 100644
--- a/quickjs.c
+++ b/quickjs.c
@@ -50325,11 +50325,26 @@ static JSValue js_typed_array_slice(JSContext *ctx, JSValue this_val,

         p1 = get_typed_array(ctx, arr, 0);
         if (p1 != NULL && p->class_id == p1->class_id &&
-            typed_array_get_length(ctx, p1) >= count &&
+// FIXME: Crash CWE-130: Improper Handling of Length Parameter Inconsistency
+             typed_array_get_length(ctx, p1) >= count &&
             typed_array_get_length(ctx, p) >= start + count) {
-            memcpy(p1->u.array.u.uint8_ptr,
-                   p->u.array.u.uint8_ptr + (start << shift),
-                   count << shift);
+            /* Check for overlap between source and destination before memcpy */
+            void *dst = p1->u.array.u.uint8_ptr;
+            void *src = p->u.array.u.uint8_ptr + (start << shift);
+            size_t len = count << shift;
+            if ((src >= (void *)((char*)dst + len)) || (dst >= (void *)((char*)src + len))) {
+                memcpy(dst, src, len);
+            } else {
+                /* fallback to element-wise copy if overlap */
+                for (n = 0; n < count; n++) {
+                    val = JS_GetPropertyValue(ctx, this_val, js_int32(start + n));
+                    if (JS_IsException(val))
+                        goto exception;
+                    if (JS_SetPropertyValue(ctx, arr, js_int32(n), val,
+                                            JS_PROP_THROW) < 0)
+                        goto exception;
+                }
+            }
         } else {
             for (n = 0; n < count; n++) {
                 val = JS_GetPropertyValue(ctx, this_val, js_int32(start + n));
