diff --git a/quickjs.c b/quickjs.c
index 7d80009..5f16242 100644
--- a/quickjs.c
+++ b/quickjs.c
@@ -26774,10 +26774,15 @@ static JSValue js_build_module_ns(JSContext *ctx, JSModuleDef *m)
         case EXPORTED_NAME_NORMAL:
             {
                 JSVarRef *var_ref = en->u.var_ref;
+                if (!var_ref) {
+                    JS_ThrowInternalError(ctx, "invalid module export reference");
+                    goto fail;
+                }
                 pr = add_property(ctx, p, en->export_name,
                                   JS_PROP_ENUMERABLE | JS_PROP_WRITABLE |
                                   JS_PROP_VARREF);
-                if (!pr)
+// FIXME: Crash CWE-476: NULL Pointer Dereference
+                 if (!pr)
                     goto fail;
                 var_ref->header.ref_count++;
                 pr->u.var_ref = var_ref;
