patch:
diff --git a/src/help.c b/src/help.c
index 28d914c82..5778f104c 100644
--- a/src/help.c
+++ b/src/help.c
@@ -422,16 +422,39 @@ find_help_tags(
 		    || (vim_strchr((char_u *)"%_z@", arg[1]) != NULL
 							   && arg[2] != NUL)))
 	{
-	    STRCPY(d, "/\\\\");
-	    STRCPY(d + 3, arg + 1);
+	    // Use safe string operations to prevent buffer overflow
+	    size_t d_len = strlen(d);
+	    size_t remaining = 1024 - d_len; // Assuming buffer size is 1024
+	    
+	    if (remaining > 4) {
+		strcpy(d + d_len, "/\\\\");
+		d_len += 3;
+		remaining -= 3;
+		
+		size_t arg_len = strlen(arg + 1);
+		if (arg_len < remaining) {
+		    strcpy(d + d_len, arg + 1);
+		} else {
+		    strncpy(d + d_len, arg + 1, remaining - 1);
+		    d[d_len + remaining - 1] = '\0';
+		}
+	    }
+	    
 	    // Check for "/\\_$", should be "/\\_\$"
-	    if (d[3] == '_' && d[4] == '$')
-		STRCPY(d + 4, "\\$");
+	    if (strlen(d) >= 5 && d[3] == '_' && d[4] == '$')
+	    {
+		size_t current_len = strlen(d);
+		if (current_len < 1022) { // Leave space for "\\$" and null terminator
+		    memmove(d + 5, d + 4, current_len - 3);
+		    d[4] = '\\';
+		    d[5] = '$';
+		}
+	    }
 	}
 	else
 	{
 	  // Replace:
-	  // "[:...:]" with "\[:...:]"
+	  // "[:...:]" with "\[:...:]" 
 	  // "[++...]" with "\[++...]"
 	  // "\{" with "\\{"		   -- matching "} \}"
 	    if ((arg[0] == '[' && (arg[1] == ':'
