patch:
diff --git a/src/normal.c b/src/normal.c
index 6cf2b9d2f..8339f6c49 100644
--- a/src/normal.c
+++ b/src/normal.c
@@ -4520,13 +4520,20 @@ nv_brackets(cmdarg_T *cap)
     else if (cap->nchar == '\'' || cap->nchar == '`')
     {
 	pos = &curwin->w_cursor;
-	for (n = cap->count1; n > 0; --n)
+// FIXME: Crash CWE-125: Out-of-bounds Read
+ 	for (n = cap->count1; n > 0; --n)
 	{
 	    prev_pos = *pos;
 	    pos = getnextmark(pos, cap->cmdchar == '[' ? BACKWARD : FORWARD,
 							  cap->nchar == '\'');
 	    if (pos == NULL)
 		break;
+	    // Additional boundary check for the mark position
+	    if (pos->lnum < 1 || pos->col < 0)
+	    {
+		pos = NULL;
+		break;
+	    }
 	}
 	if (pos == NULL)
 	    pos = &prev_pos;
