patch:
diff --git a/src/normal.c b/src/normal.c
index 6cf2b9d2f..cdcc0f549 100644
--- a/src/normal.c
+++ b/src/normal.c
@@ -7447,8 +7447,11 @@ nv_put_opt(cmdarg_T *cap, int fix_indent)
 	// characterwise, split a line when putting lines.
 	if (VIsual_mode == 'V')
 	    flags |= PUT_LINE;
-	else if (VIsual_mode == 'v')
+// Boundary check as a safety assert for 'v' visual_mode
+	else if (VIsual_mode == 'v') {
+	    assert(curwin->w_cursor.col < curbuf->b_ml.ml_line_len);
 	    flags |= PUT_LINE_SPLIT;
+	}
 	if (VIsual_mode == Ctrl_V && dir == FORWARD)
 	    flags |= PUT_LINE_FORWARD;
 	dir = BACKWARD;
