patch:
diff --git a/src/normal.c b/src/normal.c
index a929dd808..bc05c7744 100644
--- a/src/normal.c
+++ b/src/normal.c
@@ -4200,14 +4200,23 @@ normal_search(
     pos_T	prev_cursor = curwin->w_cursor;
 #endif

-    cap->oap->motion_type = MCHAR;
+// FIXME: Crash CWE-125: Out-of-bounds Read
+     cap->oap->motion_type = MCHAR;
     cap->oap->inclusive = FALSE;
     cap->oap->use_reg_one = TRUE;
     curwin->w_set_curswant = TRUE;

     CLEAR_FIELD(sia);
-    i = do_search(cap->oap, dir, dir, pat, patlen, cap->count1,
-			    opt | SEARCH_OPT | SEARCH_ECHO | SEARCH_MSG, &sia);
+    // Add bounds checking for pattern length before calling do_search
+    if (patlen > 0 && pat != NULL)
+    {
+        i = do_search(cap->oap, dir, dir, pat, patlen, cap->count1,
+                opt | SEARCH_OPT | SEARCH_ECHO | SEARCH_MSG, &sia);
+    }
+    else
+    {
+        i = 0;  // Treat invalid pattern as search failure
+    }
     if (wrapped != NULL)
 	*wrapped = sia.sa_wrapped;
     if (i == 0)
