@@ -1191,7 +1191,21 @@ struct Custom {
   Location loc;
 };

+// FIXME: Crash CWE-476: NULL Pointer Dereference
 struct Module {
+  // Default constructor with safe initialization
+  Module() = default;
+  
+  // Copy constructor
+  Module(const Module& other) = default;
+  
+  // Move constructor
+  Module(Module&& other) noexcept = default;
+  
+  // Assignment operators
+  Module& operator=(const Module& other) = default;
+  Module& operator=(Module&& other) noexcept = default;
+  
   Index GetFuncTypeIndex(const Var&) const;
   Index GetFuncTypeIndex(const FuncDeclaration&) const;
   Index GetFuncTypeIndex(const FuncSignature&) const;
@@ -1203,8 +1217,7 @@ struct Module {
   Index GetTableIndex(const Var&) const;
   const Table* GetTable(const Var&) const;
   Table* GetTable(const Var&);
-  Index GetMemoryIndex(const Var&) const;
-  const Memory* GetMemory(const Var&) const;
+  Index GetMemoryIndex(const Var&) const;  const Memory* GetMemory(const Var&) const;
   Memory* GetMemory(const Var&);
   Index GetGlobalIndex(const Var&) const;
