patch:
diff --git a/src/stream.cc b/src/stream.cc
index ad0d08a5..972e1855 100644
--- a/src/stream.cc
+++ b/src/stream.cc
@@ -52,8 +52,12 @@ void Stream::WriteDataAt(size_t at,

 void Stream::WriteData(const void* src,
                        size_t size,
-                       const char* desc,
+// FIXME: Crash CWE-476: NULL Pointer Dereference
+                        const char* desc,
                        PrintChars print_chars) {
+  if (this == nullptr) {
+    return;
+  }
   WriteDataAt(offset_, src, size, desc, print_chars);
   offset_ += size;
 }
@@ -64,8 +68,7 @@ void Stream::MoveData(size_t dst_offset, size_t src_offset, size_t size) {
   }
   if (log_stream_) {
     log_stream_->Writef(
-        "; move data: [%" PRIzx ", %" PRIzx ") -> [%" PRIzx ", %" PRIzx ")\n",
-        src_offset, src_offset + size, dst_offset, dst_offset + size);
+        "; move data: [%" PRIzx ", %" PRIzx ") -> [%" PRIzx ", %" PRIzx ")\n",        src_offset, src_offset + size, dst_offset, dst_offset + size);
   }
   result_ = MoveDataImpl(dst_offset, src_offset, size);
 }
