patch:
diff --git a/src/stream.cc b/src/stream.cc
index ad0d08a5..e62ce702 100644
--- a/src/stream.cc
+++ b/src/stream.cc
@@ -52,8 +52,13 @@ void Stream::WriteDataAt(size_t at,

 void Stream::WriteData(const void* src,
                        size_t size,
-                       const char* desc,
+// FIXME: Crash CWE-824: Access of Uninitialized Pointer
+                        const char* desc,
                        PrintChars print_chars) {
+  if (!this) {
+    // Fail gracefully if Stream is not initialized.
+    return;
+  }
   WriteDataAt(offset_, src, size, desc, print_chars);
   offset_ += size;
 }
