diff --git a/src/stream.cc b/src/stream.cc
index ad0d08a5..4567c432 100644
--- a/src/stream.cc
+++ b/src/stream.cc
@@ -47,13 +47,20 @@ void Stream::WriteDataAt(size_t at,
   if (log_stream_) {
     log_stream_->WriteMemoryDump(src, size, at, print_chars, nullptr, desc);
   }
+  if (this == nullptr) {
+    return;
+  }
   result_ = WriteDataImpl(at, src, size);
 }

 void Stream::WriteData(const void* src,
                        size_t size,
-                       const char* desc,
+// FIXME: Crash CWE-824: Access of Uninitialized Pointer
+                        const char* desc,
                        PrintChars print_chars) {
+  if (this == nullptr) {
+    return;
+  }
   WriteDataAt(offset_, src, size, desc, print_chars);
   offset_ += size;
 }
