+  const size_t MAX_FILE_SIZE = 1024 * 1024 * 1024; // 1GB limit
+  if (static_cast<size_t>(size) > MAX_FILE_SIZE) {
+    perror("file size too large");
+    fclose(infile);
+    return Result::Error;
+  }
+
   if (fseek(infile, 0, SEEK_SET) < 0) {
     perror("fseek to beginning failed");
     fclose(infile);
     return Result::Error;
-  }
