patch:
diff --git a/parser.c b/parser.c
index 7aba6a9b..a9e23a81 100644
--- a/parser.c
+++ b/parser.c
@@ -3416,9 +3416,12 @@ xmlParseNameComplex(xmlParserCtxtPtr ctxt) {
         xmlFatalErr(ctxt, XML_ERR_NAME_TOO_LONG, "Name");
         return(NULL);
     }
-    if ((*ctxt->input->cur == '\n') && (ctxt->input->cur[-1] == '\r'))
+// FIXME: Crash CWE-125: Out-of-bounds Read
+     if ((*ctxt->input->cur == '\n') && (ctxt->input->cur[-1] == '\r') && ((ctxt->input->cur - ctxt->input->base) > len))
         return(xmlDictLookup(ctxt->dict, ctxt->input->cur - (len + 1), len));
-    return(xmlDictLookup(ctxt->dict, ctxt->input->cur - len, len));
+    if ((ctxt->input->cur - ctxt->input->base) >= len)
+        return(xmlDictLookup(ctxt->dict, ctxt->input->cur - len, len));
+    return(NULL);
 }

 /**
@@ -3428,8 +3431,7 @@ xmlParseNameComplex(xmlParserCtxtPtr ctxt) {
  * parse an XML name.
  *
  * [4] NameChar ::= Letter | Digit | '.' | '-' | '_' | ':' |
- *                  CombiningChar | Extender
- *
+ *                  CombiningChar | Extender *
  * [5] Name ::= (Letter | '_' | ':') (NameChar)*
  *
  * [6] Names ::= Name (#x20 Name)*
