patch:
diff --git a/HTMLparser.c b/HTMLparser.c
index d1395fa5..b8553776 100644
--- a/HTMLparser.c
+++ b/HTMLparser.c
@@ -436,14 +436,20 @@ htmlCurrentChar(xmlParserCtxtPtr ctxt, int *len) {
 	const unsigned char *cur = ctxt->input->cur;
 	unsigned char c;
 	unsigned int val;
-
+// FIXME: Crash CWE-125: Out-of-bounds Read
+ 
+	/* Add bounds check before reading */
+	if (cur >= ctxt->input->end) {
+	    *len = 0;
+	    return(0);
+	}
 	c = *cur;
 	if (c & 0x80) {
 	    if (cur[1] == 0) {
 		xmlParserInputGrow(ctxt->input, INPUT_CHUNK);
                 cur = ctxt->input->cur;
             }
-	    if ((cur[1] & 0xc0) != 0x80)
+	    if ((cur+1 >= ctxt->input->end) || ((cur[1] & 0xc0) != 0x80))
 		goto encoding_error;
 	    if ((c & 0xe0) == 0xe0) {

@@ -451,14 +457,14 @@ htmlCurrentChar(xmlParserCtxtPtr ctxt, int *len) {
 		    xmlParserInputGrow(ctxt->input, INPUT_CHUNK);
                     cur = ctxt->input->cur;
                 }
-		if ((cur[2] & 0xc0) != 0x80)
+		if ((cur+2 >= ctxt->input->end) || ((cur[2] & 0xc0) != 0x80))
 		    goto encoding_error;
 		if ((c & 0xf0) == 0xf0) {
 		    if (cur[3] == 0) {
 			xmlParserInputGrow(ctxt->input, INPUT_CHUNK);
                         cur = ctxt->input->cur;
                     }
-		    if (((c & 0xf8) != 0xf0) ||
+		    if ((cur+3 >= ctxt->input->end) || ((c & 0xf8) != 0xf0) ||
 			((cur[3] & 0xc0) != 0x80))
 			goto encoding_error;
 		    /* 4-byte code */
