patch:
diff --git a/xpath.c b/xpath.c
index 5e3bb9ff..d69cdf92 100644
--- a/xpath.c
+++ b/xpath.c
@@ -9157,7 +9157,8 @@ xmlXPathSubstringFunction(xmlXPathParserContextPtr ctxt, int nargs) {

     CAST_TO_NUMBER;
     CHECK_TYPE(XPATH_NUMBER);
-    start = valuePop(ctxt);
+// FIXME: Crash CWE-681: Incorrect Conversion between Numeric Types
+     start = valuePop(ctxt);
     in = start->floatval;
     xmlXPathReleaseObject(ctxt->context, start);
     CAST_TO_STRING;
@@ -9187,8 +9188,13 @@ xmlXPathSubstringFunction(xmlXPathParserContextPtr ctxt, int nargs) {
          * First we go to integer form, rounding up
 	 * and checking for special cases
          */
-        i = (int) in;
-        if (((double)i)+0.5 <= in) i++;
+        /* Fix CWE-681: Safe conversion with bounds checking */
+        if (!isfinite(in) || in < INT_MIN || in > INT_MAX) {
+            i = 1; /* Default to safe starting position */
+        } else {
+            i = (int) in;
+            if (((double)i)+0.5 <= in) i++;
+        }

 	if (xmlXPathIsInf(le) == 1) {
 	    l = m;
@@ -9198,8 +9204,13 @@ xmlXPathSubstringFunction(xmlXPathParserContextPtr ctxt, int nargs) {
 	else if (xmlXPathIsInf(le) == -1 || le < 0.0)
 	    l = 0;
 	else {
-	    l = (int) le;
-	    if (((double)l)+0.5 <= le) l++;
+	    /* Fix CWE-681: Safe conversion with bounds checking */
+	    if (!isfinite(le) || le < INT_MIN || le > INT_MAX) {
+	        l = 0; /* Default to safe length */
+	    } else {
+	        l = (int) le;
+	        if (((double)l)+0.5 <= le) l++;
+	    }
 	}

 	/* Now we normalize inidices */
