patch:
diff --git a/xpath.c b/xpath.c
index 5e3bb9ff..364d647b 100644
--- a/xpath.c
+++ b/xpath.c
@@ -9157,7 +9157,8 @@ xmlXPathSubstringFunction(xmlXPathParserContextPtr ctxt, int nargs) {

     CAST_TO_NUMBER;
     CHECK_TYPE(XPATH_NUMBER);
-    start = valuePop(ctxt);
+// FIXME: Crash CWE-681: Incorrect Conversion between Numeric Types
+     start = valuePop(ctxt);
     in = start->floatval;
     xmlXPathReleaseObject(ctxt->context, start);
     CAST_TO_STRING;
@@ -9187,7 +9188,11 @@ xmlXPathSubstringFunction(xmlXPathParserContextPtr ctxt, int nargs) {
          * First we go to integer form, rounding up
 	 * and checking for special cases
          */
-        i = (int) in;
+        if (xmlXPathIsNaN(in) || in > 2147483647.0 || in < -2147483648.0) {
+            i = 0;
+        } else {
+            i = (int) in;
+        }
         if (((double)i)+0.5 <= in) i++;

 	if (xmlXPathIsInf(le) == 1) {
@@ -9198,7 +9203,11 @@ xmlXPathSubstringFunction(xmlXPathParserContextPtr ctxt, int nargs) {
 	else if (xmlXPathIsInf(le) == -1 || le < 0.0)
 	    l = 0;
 	else {
-	    l = (int) le;
+	    if (xmlXPathIsNaN(le) || le > 2147483647.0 || le < -2147483648.0) {
+                l = 0;
+            } else {
+                l = (int) le;
+            }
 	    if (((double)l)+0.5 <= le) l++;
 	}
