patch:
diff --git a/xpath.c b/xpath.c
index 5e3bb9ff..8e4d0dfd 100644
--- a/xpath.c
+++ b/xpath.c
@@ -9185,9 +9185,13 @@ xmlXPathSubstringFunction(xmlXPathParserContextPtr ctxt, int nargs) {
 	 * initial index calculations are done
          *
          * First we go to integer form, rounding up
-	 * and checking for special cases
+         * and checking for special cases
          */
-        i = (int) in;
+        if (in >= INT_MIN && in <= INT_MAX) {
+            i = (int) in;
+        } else {
+            i = (in < 0) ? 0 : m;
+        }
         if (((double)i)+0.5 <= in) i++;

 	if (xmlXPathIsInf(le) == 1) {
@@ -9198,7 +9202,11 @@ xmlXPathSubstringFunction(xmlXPathParserContextPtr ctxt, int nargs) {
 	else if (xmlXPathIsInf(le) == -1 || le < 0.0)
 	    l = 0;
 	else {
-	    l = (int) le;
+	    if (le >= INT_MIN && le <= INT_MAX) {
+	        l = (int) le;
+	    } else {
+	        l = (le < 0) ? 0 : m;
+	    }
 	    if (((double)l)+0.5 <= le) l++;
 	}
