patch:
diff --git a/xpath.c b/xpath.c
index 5e3bb9ff..50203d72 100644
--- a/xpath.c
+++ b/xpath.c
@@ -9157,7 +9157,8 @@ xmlXPathSubstringFunction(xmlXPathParserContextPtr ctxt, int nargs) {

     CAST_TO_NUMBER;
     CHECK_TYPE(XPATH_NUMBER);
-    start = valuePop(ctxt);
+// FIXME: Crash CWE-681: Incorrect Conversion between Numeric Types
+     start = valuePop(ctxt);
     in = start->floatval;
     xmlXPathReleaseObject(ctxt->context, start);
     CAST_TO_STRING;
@@ -9187,6 +9188,12 @@ xmlXPathSubstringFunction(xmlXPathParserContextPtr ctxt, int nargs) {
          * First we go to integer form, rounding up
 	 * and checking for special cases
          */
+        /* PATCH: Bail out if out of range instead of capping */
+        if (in > (double)INT_MAX || in < (double)INT_MIN) {
+            valuePush(ctxt, xmlXPathCacheNewCString(ctxt->context, ""));
+            xmlXPathReleaseObject(ctxt->context, str);
+            return;
+        }
         i = (int) in;
         if (((double)i)+0.5 <= in) i++;

@@ -9198,8 +9205,13 @@ xmlXPathSubstringFunction(xmlXPathParserContextPtr ctxt, int nargs) {
 	else if (xmlXPathIsInf(le) == -1 || le < 0.0)
 	    l = 0;
 	else {
-	    l = (int) le;
-	    if (((double)l)+0.5 <= le) l++;
+            if (le > (double)INT_MAX || le < (double)INT_MIN) {
+                valuePush(ctxt, xmlXPathCacheNewCString(ctxt->context, ""));
+                xmlXPathReleaseObject(ctxt->context, str);
+                return;
+            }
+            l = (int) le;
+            if (((double)l)+0.5 <= le) l++;
 	}

 	/* Now we normalize inidices */
@@ -9227,6 +9239,7 @@ xmlXPathSubstringFunction(xmlXPathParserContextPtr ctxt, int nargs) {
     xmlXPathReleaseObject(ctxt->context, str);
 }

+
 /**
  * xmlXPathSubstringBeforeFunction:
  * @ctxt:  the XPath Parser context
