diff --git a/buf.c b/buf.c
index 07922ff6..64ddd00b 100644
--- a/buf.c
+++ b/buf.c
@@ -508,13 +508,21 @@ xmlBufGrowInternal(xmlBufPtr buf, size_t len) {
 int
 xmlBufGrow(xmlBufPtr buf, int len) {
     size_t ret;
+    int buffer_relocated = 0;

     if ((buf == NULL) || (len < 0)) return(-1);
     if (len == 0)
         return(0);
+    /* Store original buffer address to detect reallocation */
+    void *original_addr = buf->content;
     ret = xmlBufGrowInternal(buf, len);
     if (buf->error != 0)
         return(-1);
+    /* Detect buffer reallocation */
+    if (buf->content != original_addr) {
+        buffer_relocated = 1;
+        /* Buffer was reallocated - any existing pointers are now invalid */
+    }
     return((int) ret);
 }

diff --git a/parser.c b/parser.c
index 4464e2e8..16987d57 100644
--- a/parser.c
+++ b/parser.c
@@ -3522,18 +3522,21 @@ xmlParseNCNameComplex(xmlParserCtxtPtr ctxt) {
 	    c = CUR_CHAR(l);
 	}
     }
-    if ((len > XML_MAX_NAME_LENGTH) &&
+        if ((len > XML_MAX_NAME_LENGTH) &&
         ((ctxt->options & XML_PARSE_HUGE) == 0)) {
         xmlFatalErr(ctxt, XML_ERR_NAME_TOO_LONG, "NCName");
         return(NULL);
     }
+    /* Ensure the name data is within current buffer bounds */
+    if (end - len < ctxt->input->base || end > ctxt->input->end) {
+        return(NULL);
+    }
     return(xmlDictLookup(ctxt->dict, end - len, len));
 }

 /**
  * xmlParseNCName:
- * @ctxt:  an XML parser context
- * @len:  length of the string parsed
+ * @ctxt:  an XML parser context * @len:  length of the string parsed
  *
  * parse an XML name.
  *
