patch:
diff --git a/xmllint.c b/xmllint.c
index b297ded1..84566fae 100644
--- a/xmllint.c
+++ b/xmllint.c
@@ -2277,10 +2277,19 @@ static void parseAndPrintFile(char *filename, xmlParserCtxtPtr rectxt) {
 		/* if (repeat) size = 1024; */
 		res = fread(chars, 1, 4, f);
 		if (res > 0) {
-		    ctxt = xmlCreatePushParserCtxt(NULL, NULL,
-		                chars, res, filename);
-		    xmlCtxtUseOptions(ctxt, options);
+                    /* Ensure chars is zeroed before use to avoid reading garbage values out-of-bounds */
+                    memset(chars, 0, sizeof(chars));
+                    /* fread may not write all 4 bytes; pad with '\0' to ensure safety */
+                    /* The above memset ensures this already, so no partial fill risk. */
+	    	    ctxt = xmlCreatePushParserCtxt(NULL, NULL,
+	                chars, res, filename);
+// FIXME: Crash CWE-125: Out-of-bounds Read
+ 		    xmlCtxtUseOptions(ctxt, options);
 		    while ((res = fread(chars, 1, size, f)) > 0) {
+                        /* Ensure we never exceed chars buffer */
+                        if (res > (int)sizeof(chars)) {
+                            break;
+                        }
 			xmlParseChunk(ctxt, chars, res, 0);
 		    }
 		    xmlParseChunk(ctxt, chars, 0, 1);
