patch:
diff --git a/xpath.c b/xpath.c
index a60a6237..fa82f22d 100644
--- a/xpath.c
+++ b/xpath.c
@@ -8975,6 +8975,11 @@ xmlXPathStringFunction(xmlXPathParserContextPtr ctxt, int nargs) {
     CHECK_ARITY(1);
     cur = valuePop(ctxt);
     if (cur == NULL) XP_ERROR(XPATH_INVALID_OPERAND);
+    /* Overflow prevention: Check for extreme values that may cause conversion errors */
+    if (cur->type == XPATH_NUMBER && (fabs(cur->floatval) > LONG_MAX)) {
+        xmlXPathReleaseObject(ctxt->context, cur);
+        XP_ERROR(XPATH_NUMBER_ERROR);
+    }
     valuePush(ctxt, xmlXPathCacheConvertString(ctxt->context, cur));
 }
