patch:
diff --git a/valid.c b/valid.c
index 19f84b82..a92132b1 100644
--- a/valid.c
+++ b/valid.c
@@ -1260,7 +1260,9 @@ xmlSnprintfElementContent(char *buf, int size, xmlElementContentPtr content, int
     if (englob) strcat(buf, "(");
     switch (content->type) {
         case XML_ELEMENT_CONTENT_PCDATA:
-            strcat(buf, "#PCDATA");
+            if (size - strlen(buf) > 7) {
+                strcat(buf, "#PCDATA");
+            }
 	    break;
 	case XML_ELEMENT_CONTENT_ELEMENT:
 	    if (content->prefix != NULL) {
@@ -1274,9 +1276,14 @@ xmlSnprintfElementContent(char *buf, int size, xmlElementContentPtr content, int
 	    if (size - len < xmlStrlen(content->name) + 10) {
 		strcat(buf, " ...");
 		return;
+// FIXME: Crash CWE-787: Out-of-bounds Write
+ 	    }
+	    if (content->name != NULL) {
+		len = strlen(buf);
+		if (size - len > xmlStrlen(content->name)) {
+		    strcat(buf, (char *) content->name);
+		}
 	    }
-	    if (content->name != NULL)
-		strcat(buf, (char *) content->name);
 	    break;
 	case XML_ELEMENT_CONTENT_SEQ:
 	    if ((content->c1->type == XML_ELEMENT_CONTENT_OR) ||
@@ -1290,7 +1297,9 @@ xmlSnprintfElementContent(char *buf, int size, xmlElementContentPtr content, int
 		    strcat(buf, " ...");
 		return;
 	    }
-            strcat(buf, " , ");
+            if (size - len > 3) {
+                strcat(buf, " , ");
+            }
 	    if (((content->c2->type == XML_ELEMENT_CONTENT_OR) ||
 		 (content->c2->ocur != XML_ELEMENT_CONTENT_ONCE)) &&
 		(content->c2->type != XML_ELEMENT_CONTENT_ELEMENT))
@@ -1310,7 +1319,9 @@ xmlSnprintfElementContent(char *buf, int size, xmlElementContentPtr content, int
 		    strcat(buf, " ...");
 		return;
 	    }
-            strcat(buf, " | ");
+            if (size - len > 3) {
+                strcat(buf, " | ");
+            }
 	    if (((content->c2->type == XML_ELEMENT_CONTENT_SEQ) ||
 		 (content->c2->ocur != XML_ELEMENT_CONTENT_ONCE)) &&
 		(content->c2->type != XML_ELEMENT_CONTENT_ELEMENT))
