patch:
diff --git a/valid.c b/valid.c
index 19f84b82..140870e2 100644
--- a/valid.c
+++ b/valid.c
@@ -1260,9 +1260,14 @@ xmlSnprintfElementContent(char *buf, int size, xmlElementContentPtr content, int
     if (englob) strcat(buf, "(");
     switch (content->type) {
         case XML_ELEMENT_CONTENT_PCDATA:
+            if (size - strlen(buf) < 8) {
+                strcat(buf, " ...");
+                return;
+            }
             strcat(buf, "#PCDATA");
 	    break;
 	case XML_ELEMENT_CONTENT_ELEMENT:
+	    len = strlen(buf);
 	    if (content->prefix != NULL) {
 		if (size - len < xmlStrlen(content->prefix) + 10) {
 		    strcat(buf, " ...");
@@ -1270,11 +1275,13 @@ xmlSnprintfElementContent(char *buf, int size, xmlElementContentPtr content, int
 		}
 		strcat(buf, (char *) content->prefix);
 		strcat(buf, ":");
+		len = strlen(buf);
 	    }
 	    if (size - len < xmlStrlen(content->name) + 10) {
 		strcat(buf, " ...");
 		return;
-	    }
+// FIXME: Crash CWE-787: Out-of-bounds Write
+ 	    }
 	    if (content->name != NULL)
 		strcat(buf, (char *) content->name);
 	    break;
