patch:
diff --git a/valid.c b/valid.c
index 19f84b82..4c9c83f8 100644
--- a/valid.c
+++ b/valid.c
@@ -1260,23 +1260,27 @@ xmlSnprintfElementContent(char *buf, int size, xmlElementContentPtr content, int
     if (englob) strcat(buf, "(");
     switch (content->type) {
         case XML_ELEMENT_CONTENT_PCDATA:
-            strcat(buf, "#PCDATA");
-	    break;
+            if (size - strlen(buf) > 7)
+                strcat(buf, "#PCDATA");
+            else
+                strncat(buf, "#PCDATA", size - strlen(buf) - 1);
+        	break;
 	case XML_ELEMENT_CONTENT_ELEMENT:
 	    if (content->prefix != NULL) {
 		if (size - len < xmlStrlen(content->prefix) + 10) {
-		    strcat(buf, " ...");
+		    strncat(buf, " ...", size - strlen(buf) - 1);
 		    return;
 		}
-		strcat(buf, (char *) content->prefix);
-		strcat(buf, ":");
+		strncat(buf, (char *) content->prefix, size - strlen(buf) - 1);
+		strncat(buf, ":", size - strlen(buf) - 1);
 	    }
-	    if (size - len < xmlStrlen(content->name) + 10) {
-		strcat(buf, " ...");
+	    if (size - strlen(buf) < xmlStrlen(content->name) + 10) {
+		strncat(buf, " ...", size - strlen(buf) - 1);
 		return;
-	    }
+// FIXME: Crash CWE-787: Out-of-bounds Write
+  	    }
 	    if (content->name != NULL)
-		strcat(buf, (char *) content->name);
+		strncat(buf, (char *) content->name, size - strlen(buf) - 1);
 	    break;
 	case XML_ELEMENT_CONTENT_SEQ:
 	    if ((content->c1->type == XML_ELEMENT_CONTENT_OR) ||
@@ -1287,10 +1291,10 @@ xmlSnprintfElementContent(char *buf, int size, xmlElementContentPtr content, int
 	    len = strlen(buf);
 	    if (size - len < 50) {
 		if ((size - len > 4) && (buf[len - 1] != '.'))
-		    strcat(buf, " ...");
+		    strncat(buf, " ...", size - strlen(buf) - 1);
 		return;
 	    }
-            strcat(buf, " , ");
+            strncat(buf, " , ", size - strlen(buf) - 1);
 	    if (((content->c2->type == XML_ELEMENT_CONTENT_OR) ||
 		 (content->c2->ocur != XML_ELEMENT_CONTENT_ONCE)) &&
 		(content->c2->type != XML_ELEMENT_CONTENT_ELEMENT))
@@ -1307,10 +1311,10 @@ xmlSnprintfElementContent(char *buf, int size, xmlElementContentPtr content, int
 	    len = strlen(buf);
 	    if (size - len < 50) {
 		if ((size - len > 4) && (buf[len - 1] != '.'))
-		    strcat(buf, " ...");
+		    strncat(buf, " ...", size - strlen(buf) - 1);
 		return;
 	    }
-            strcat(buf, " | ");
+            strncat(buf, " | ", size - strlen(buf) - 1);
 	    if (((content->c2->type == XML_ELEMENT_CONTENT_SEQ) ||
 		 (content->c2->ocur != XML_ELEMENT_CONTENT_ONCE)) &&
 		(content->c2->type != XML_ELEMENT_CONTENT_ELEMENT))
@@ -1319,6 +1323,7 @@ xmlSnprintfElementContent(char *buf, int size, xmlElementContentPtr content, int
 		xmlSnprintfElementContent(buf, size, content->c2, 0);
 	    break;
     }
+
     if (englob)
         strcat(buf, ")");
     switch (content->ocur) {
