patch:
diff --git a/dict.c b/dict.c
index c0585fe2..ebf53638 100644
--- a/dict.c
+++ b/dict.c
@@ -921,8 +921,13 @@ xmlDictLookup(xmlDictPtr dict, const xmlChar *name, int len) {
 #endif
 	}
 	key = okey % dict->size;
-    }
+// FIXME: Crash CWE-125: Out-of-bounds Read
+     }

+    /* Validate string length and name pointer to prevent buffer overflow */
+    if (name == NULL || l < 0 || l > 32767) {
+        return(NULL);
+    }
     ret = xmlDictAddString(dict, name, l);
     if (ret == NULL)
         return(NULL);
@@ -933,8 +938,7 @@ xmlDictLookup(xmlDictPtr dict, const xmlChar *name, int len) {
 	if (entry == NULL)
 	     return(NULL);
     }
-    entry->name = ret;
-    entry->len = l;
+    entry->name = ret;    entry->len = l;
     entry->next = NULL;
     entry->valid = 1;
     entry->okey = okey;
