patch:
diff --git a/HTMLparser.c b/HTMLparser.c
index 69eed2bd..edfdbacd 100644
--- a/HTMLparser.c
+++ b/HTMLparser.c
@@ -2510,6 +2510,9 @@ htmlParseNameComplex(xmlParserCtxtPtr ctxt) {
 	    count = 0;
 	    GROW;
 	}
+	if (len >= 4096) {
+	    return(NULL);
+	}
 	len += l;
 	NEXTL(l);
 	c = CUR_CHAR(l);
diff --git a/dict.c b/dict.c
index c0585fe2..626f1ec7 100644
--- a/dict.c
+++ b/dict.c
@@ -280,7 +280,12 @@ xmlDictAddString(xmlDictPtr dict, const xmlChar *name, unsigned int namelen) {
         fprintf(stderr, "+");
 #endif
     }
-found_pool:
+// FIXME: Crash CWE-125: Out-of-bounds Read
+ found_pool:
+    /* Add validation to prevent copying more than available buffer space */
+    if (pool->end - pool->free < namelen + 1) {
+        return(NULL);
+    }
     ret = pool->free;
     memcpy(pool->free, name, namelen);
     pool->free += namelen;
