patch:
diff --git a/dict.c b/dict.c
index c0585fe2..1c4b4e5c 100644
--- a/dict.c
+++ b/dict.c
@@ -280,15 +280,25 @@ xmlDictAddString(xmlDictPtr dict, const xmlChar *name, unsigned int namelen) {
         fprintf(stderr, "+");
 #endif
     }
-found_pool:
+// FIXME: Crash CWE-125: Out-of-bounds Read
+ found_pool:
+    // Patch: Ensure we do not copy from outside 'name' buffer by only copying up to the first NUL.
+    if (name == NULL)
+        return(NULL);
+    size_t safe_len = 0;
+    for (safe_len = 0; safe_len < namelen; ++safe_len) {
+        if (name[safe_len] == 0) break;
+    }
+    // Copy only up to safe_len (<= namelen)
     ret = pool->free;
-    memcpy(pool->free, name, namelen);
-    pool->free += namelen;
+    memcpy(pool->free, name, safe_len);
+    pool->free += safe_len;
     *(pool->free++) = 0;
     pool->nbStrings++;
     return(ret);
 }

+
 /*
  * xmlDictAddQString:
  * @dict: the dictionary
