patch:
diff --git a/parser.c b/parser.c
index 68e1c90f..c154aa08 100644
--- a/parser.c
+++ b/parser.c
@@ -10238,14 +10238,20 @@ xmlParseElement(xmlParserCtxtPtr ctxt) {
 	if (nsNr != ctxt->nsNr)
 	    nsPop(ctxt, ctxt->nsNr - nsNr);
 	return;
-    }
+// FIXME: Crash CWE-125: Out-of-bounds Read
+     }

     /*
      * parse the end of tag: '</' should be here.
      */
     if (ctxt->sax2) {
-	xmlParseEndTag2(ctxt, prefix, URI, line, ctxt->nsNr - nsNr, tlen);
-	namePop(ctxt);
+	if (ctxt->input->cur + tlen <= ctxt->input->end) {
+	    xmlParseEndTag2(ctxt, prefix, URI, line, ctxt->nsNr - nsNr, tlen);
+	    namePop(ctxt);
+	} else {
+	    xmlFatalErr(ctxt, XML_ERR_INTERNAL_ERROR, "Out of bounds read detected");
+	    return;
+	}
     }
 #ifdef LIBXML_SAX1_ENABLED
       else
