diff --git a/parser.c b/parser.c
index 68e1c90f..76d5b319 100644
--- a/parser.c
+++ b/parser.c
@@ -9832,14 +9832,18 @@ xmlParseEndTag2(xmlParserCtxtPtr ctxt, const xmlChar *prefix,
 	return;
     }
     SKIP(2);
-
+// FIXME: Crash CWE-125: Out-of-bounds Read
+ 
     if ((tlen > 0) && (xmlStrncmp(ctxt->input->cur, ctxt->name, tlen) == 0)) {
-        if (ctxt->input->cur[tlen] == '>') {
+        if ((ctxt->input->cur + tlen < ctxt->input->end) && (ctxt->input->cur[tlen] == '>')) {
 	    ctxt->input->cur += tlen + 1;
 	    ctxt->input->col += tlen + 1;
 	    goto done;
 	}
-	ctxt->input->cur += tlen;
+	if (ctxt->input->cur + tlen <= ctxt->input->end)
+	    ctxt->input->cur += tlen;
+	else
+	    ctxt->input->cur = ctxt->input->end;
 	ctxt->input->col += tlen;
 	name = (xmlChar*)1;
     } else {
@@ -9872,8 +9876,8 @@ xmlParseEndTag2(xmlParserCtxtPtr ctxt, const xmlChar *prefix,
         if ((line == 0) && (ctxt->node != NULL))
             line = ctxt->node->line;
         xmlFatalErrMsgStrIntStr(ctxt, XML_ERR_TAG_NAME_MISMATCH,
-		     "Opening and ending tag mismatch: %s line %d and %s\n",
-		                ctxt->name, line, name);
+	     "Opening and ending tag mismatch: %s line %d and %s\n",
+	                ctxt->name, line, name);
     }

     /*
@@ -9890,6 +9894,7 @@ done:
     return;
 }

