patch:
diff --git a/xpath.c b/xpath.c
index 82b0eeaf..6816dd53 100644
--- a/xpath.c
+++ b/xpath.c
@@ -10244,7 +10244,13 @@ xmlXPathCompNumber(xmlXPathParserContextPtr ctxt)
         } else if (CUR == '+') {
 	    NEXT;
 	}
-        while ((CUR >= '0') && (CUR <= '9')) {
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+         while ((CUR >= '0') && (CUR <= '9')) {
+            if (exponent > INT_MAX / 10) {
+                /* Prevent integer overflow */
+                exponent = INT_MAX;
+                break;
+            }
             exponent = exponent * 10 + (CUR - '0');
             NEXT;
         }
