patch:
diff --git a/xpath.c b/xpath.c
index 82b0eeaf..2515b6b3 100644
--- a/xpath.c
+++ b/xpath.c
@@ -10244,7 +10244,16 @@ xmlXPathCompNumber(xmlXPathParserContextPtr ctxt)
         } else if (CUR == '+') {
 	    NEXT;
 	}
-        while ((CUR >= '0') && (CUR <= '9')) {
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+         while ((CUR >= '0') && (CUR <= '9')) {
+            if (exponent > (INT_MAX - (CUR - '0')) / 10) {
+                /* Integer overflow would occur, skip remaining digits */
+                exponent = INT_MAX;
+                while ((CUR >= '0') && (CUR <= '9')) {
+                    NEXT;
+                }
+                break;
+            }
             exponent = exponent * 10 + (CUR - '0');
             NEXT;
         }
