patch:
diff --git a/xpath.c b/xpath.c
index 82b0eeaf..4867599e 100644
--- a/xpath.c
+++ b/xpath.c
@@ -10244,7 +10244,14 @@ xmlXPathCompNumber(xmlXPathParserContextPtr ctxt)
         } else if (CUR == '+') {
 	    NEXT;
 	}
-        while ((CUR >= '0') && (CUR <= '9')) {
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+         while ((CUR >= '0') && (CUR <= '9')) {
+            /* Check for overflow before multiplication */
+            if (exponent > (INT_MAX - 9) / 10) {
+                /* Cap the exponent at a safe maximum */
+                exponent = 1000000;
+                break;
+            }
             exponent = exponent * 10 + (CUR - '0');
             NEXT;
         }
