patch:
diff --git a/xpath.c b/xpath.c
index 82b0eeaf..4a5593b2 100644
--- a/xpath.c
+++ b/xpath.c
@@ -10243,8 +10243,12 @@ xmlXPathCompNumber(xmlXPathParserContextPtr ctxt)
             NEXT;
         } else if (CUR == '+') {
 	    NEXT;
-	}
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+ 	}
         while ((CUR >= '0') && (CUR <= '9')) {
+            if (exponent > INT_MAX/10 || (exponent == INT_MAX/10 && (CUR - '0') > INT_MAX%10)) {
+                XP_ERROR(XPATH_NUMBER_ERROR);
+            }
             exponent = exponent * 10 + (CUR - '0');
             NEXT;
         }
