patch:
diff --git a/xpath.c b/xpath.c
index 82b0eeaf..ad10fa65 100644
--- a/xpath.c
+++ b/xpath.c
@@ -10243,8 +10243,15 @@ xmlXPathCompNumber(xmlXPathParserContextPtr ctxt)
             NEXT;
         } else if (CUR == '+') {
 	    NEXT;
-	}
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+ 	}
         while ((CUR >= '0') && (CUR <= '9')) {
+            /* Check for overflow before multiplication */
+            if (exponent > (INT_MAX - (CUR - '0')) / 10) {
+                /* Overflow would occur, limit exponent to maximum safe value */
+                exponent = INT_MAX / 10;
+                break;
+            }
             exponent = exponent * 10 + (CUR - '0');
             NEXT;
         }
