patch:
diff --git a/xpath.c b/xpath.c
index 82b0eeaf..872e96b6 100644
--- a/xpath.c
+++ b/xpath.c
@@ -10244,10 +10244,19 @@ xmlXPathCompNumber(xmlXPathParserContextPtr ctxt)
         } else if (CUR == '+') {
 	    NEXT;
 	}
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+        int64_t exp_accum = 0;
         while ((CUR >= '0') && (CUR <= '9')) {
-            exponent = exponent * 10 + (CUR - '0');
+            if (exp_accum > (INT_MAX/10) || (exp_accum == (INT_MAX/10) && (CUR - '0') > (INT_MAX%10))) {
+                XP_ERROR(XPATH_NUMBER_ERROR);
+            }
+            exp_accum = exp_accum * 10 + (CUR - '0');
             NEXT;
         }
+        if (exp_accum > INT_MAX) {
+            XP_ERROR(XPATH_NUMBER_ERROR);
+        }
+        exponent = (int)exp_accum;
         if (is_exponent_negative)
             exponent = -exponent;
         ret *= pow(10.0, (double) exponent);
@@ -10256,6 +10265,7 @@ xmlXPathCompNumber(xmlXPathParserContextPtr ctxt)
                    xmlXPathCacheNewFloat(ctxt->context, ret), NULL);
 }

+
 /**
  * xmlXPathParseLiteral:
  * @ctxt:  the XPath Parser context
