patch:
diff --git a/xpath.c b/xpath.c
index 82b0eeaf..f6212270 100644
--- a/xpath.c
+++ b/xpath.c
@@ -10243,8 +10243,15 @@ xmlXPathCompNumber(xmlXPathParserContextPtr ctxt)
             NEXT;
         } else if (CUR == '+') {
 	    NEXT;
+	// FIXME: Crash CWE-190: Integer Overflow or Wraparound
 	}
         while ((CUR >= '0') && (CUR <= '9')) {
+            /* Protect against integer overflow */
+            if (exponent > (INT_MAX - (CUR - '0')) / 10) {
+                exponent = INT_MAX;
+                while ((CUR >= '0') && (CUR <= '9')) NEXT;
+                break;
+            }
             exponent = exponent * 10 + (CUR - '0');
             NEXT;
         }
