patch:
diff --git a/xpath.c b/xpath.c
index 82b0eeaf..b31a5886 100644
--- a/xpath.c
+++ b/xpath.c
@@ -10244,9 +10244,14 @@ xmlXPathCompNumber(xmlXPathParserContextPtr ctxt)
         } else if (CUR == '+') {
 	    NEXT;
 	}
-        while ((CUR >= '0') && (CUR <= '9')) {
-            exponent = exponent * 10 + (CUR - '0');
-            NEXT;
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+         while ((CUR >= '0') && (CUR <= '9')) {
+            if (exponent > (INT_MAX - 9) / 10) {
+                exponent = INT_MAX; /* or you could handle error here */
+            } else {
+                exponent = exponent * 10 + (CUR - '0');
+                NEXT;
+            }
         }
         if (is_exponent_negative)
             exponent = -exponent;
