patch:
diff --git a/xpath.c b/xpath.c
index 82b0eeaf..ca165cfb 100644
--- a/xpath.c
+++ b/xpath.c
@@ -10243,8 +10243,16 @@ xmlXPathCompNumber(xmlXPathParserContextPtr ctxt)
             NEXT;
         } else if (CUR == '+') {
 	    NEXT;
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
 	}
         while ((CUR >= '0') && (CUR <= '9')) {
+            if (exponent > (INT_MAX - (CUR - '0')) / 10) {
+                /* Prevent integer overflow */
+                exponent = INT_MAX;
+                while ((CUR >= '0') && (CUR <= '9'))
+                    NEXT;
+                break;
+            }
             exponent = exponent * 10 + (CUR - '0');
             NEXT;
         }
@@ -10256,6 +10264,7 @@ xmlXPathCompNumber(xmlXPathParserContextPtr ctxt)
                    xmlXPathCacheNewFloat(ctxt->context, ret), NULL);
 }

+
 /**
  * xmlXPathParseLiteral:
  * @ctxt:  the XPath Parser context
