patch:
diff --git a/xpath.c b/xpath.c
index 82b0eeaf..84b4cced 100644
--- a/xpath.c
+++ b/xpath.c
@@ -10243,9 +10243,17 @@ xmlXPathCompNumber(xmlXPathParserContextPtr ctxt)
             NEXT;
         } else if (CUR == '+') {
 	    NEXT;
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
 	}
         while ((CUR >= '0') && (CUR <= '9')) {
-            exponent = exponent * 10 + (CUR - '0');
+            int digit = (CUR - '0');
+            if (exponent > (INT_MAX - digit) / 10) {
+                exponent = INT_MAX;
+                while ((CUR >= '0') && (CUR <= '9'))
+                    NEXT;
+                break;
+            }
+            exponent = exponent * 10 + digit;
             NEXT;
         }
         if (is_exponent_negative)
@@ -10256,6 +10264,7 @@ xmlXPathCompNumber(xmlXPathParserContextPtr ctxt)
                    xmlXPathCacheNewFloat(ctxt->context, ret), NULL);
 }

+
 /**
  * xmlXPathParseLiteral:
  * @ctxt:  the XPath Parser context
